import React, { useState } from 'react';
import axios from 'axios';
import {
  Container, TextField, Button, Typography, Box, Paper
} from '@mui/material';

const ShortenPage = () => {
  const [originalUrl, setOriginalUrl] = useState('');
  const [validity, setValidity] = useState('');
  const [customCode, setCustomCode] = useState('');
  const [shortUrl, setShortUrl] = useState('');
  const [error, setError] = useState('');

  const handleShorten = async () => {
    if (!originalUrl) return setError('Original URL required');
    try {
      const res = await axios.post('http://localhost:5000/shorten', {
        originalUrl,
        validity: validity ? parseInt(validity) : undefined,
        customCode,
      });
      setShortUrl(res.data.shortUrl);
      setError('');
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to shorten');
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 5 }}>
      <Paper elevation={6} sx={{ p: 4, borderRadius: 3, backgroundColor: 'background.paper' }}>
        <Typography variant="h3" gutterBottom align="center" sx={{ fontWeight: 'bold', mb: 3 }}>
          URL Shortener
        </Typography>
        <TextField
          fullWidth
          label="Original URL"
          margin="normal"
          value={originalUrl}
          onChange={e => setOriginalUrl(e.target.value)}
          sx={{ mb: 2 }}
        />
        <TextField
          fullWidth
          label="Validity (minutes)"
          margin="normal"
          value={validity}
          onChange={e => setValidity(e.target.value)}
          sx={{ mb: 2 }}
        />
        <TextField
          fullWidth
          label="Custom Shortcode (optional)"
          margin="normal"
          value={customCode}
          onChange={e => setCustomCode(e.target.value)}
          sx={{ mb: 3 }}
        />
        <Button variant="contained" onClick={handleShorten} fullWidth size="large" sx={{ fontWeight: 'bold' }}>
          Shorten
        </Button>
        {shortUrl && (
          <Box mt={3} textAlign="center">
            <Typography variant="h6">
              Short URL: <a href={shortUrl}>{shortUrl}</a>
            </Typography>
          </Box>
        )}
        {error && (
          <Typography color="error" mt={2} align="center" sx={{ fontWeight: 'medium' }}>
            {error}
          </Typography>
        )}
      </Paper>
    </Container>
  );
};

export default ShortenPage;
